import React from "react";
import styled from 'styled-components'
import { Button, Button1, Button2, GetNotified, ReadMore, Checkbox, LoginText, EmailInput,PassInput, Forget_password, NewTo} from "./Style.module";

import { Container } from "./Style.module";

const Body = styled.div`
    background-color: whitesmoke;
`
const Login = () => {
  const [formDetails, setFormDetails] = React.useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormDetails({
      ...formDetails,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.prevenDefault();
  };

  const { email, password } = formDetails;
  return (
    <Body>
      <Container>
        <form onSubmit={handleSubmit}>
          <LoginText>Log in</LoginText>
          <EmailInput><input
            type="text"
            placeholder="Email"
            value={email}
            onChange={handleChange}
          /></EmailInput>
          <br />
          <PassInput><input
            type="text"
            placeholder="password"
            value={password}
            onChange={handleChange}
          /></PassInput>
          <br />
          <Forget_password>Forgot your password?</Forget_password>
         
          <Button>Log in</Button> <br />
         
          <Checkbox> <input type="checkbox" /> Remember me</Checkbox> or <br/>
          
          <Button1><i className="fa-brands fa-apple"> Sign in with Apple</i></Button1>
          <br />
          <Button2><i class="fa-brands fa-facebook-square"></i> Continue with Facebook</Button2>
          <br />

          <GetNotified>
          Get notified when your friends back and launch projects.<br/> We'll never post anything on Facebook without your permission.
          </GetNotified>
          <ReadMore> Read more </ReadMore>
          <hr />
          <NewTo>New to Kickstarter?</NewTo> <Si href="#"> Sign up</Si>
          <hr />
          <p>
            This site is protected by reCAPTCHA and the Google
            <a href="#">
              {" "}
              Privacy Policy
              <br />{" "}
            </a>{" "}
            and
            <a href="#"> Terms of service</a> apply.
          </p>
        </form>
      </Container>
    </Body>
  );
};

export default Login;
