import React,{useState} from "react";
import { Link } from "react-router-dom";

const SignUp = () => {
  const [userSignUp, setuserSignUp] = useState({
    name: "",
    email: "",
    password: "",
  });

  const handleChange = (e) =>{
    const {name, value} = e.target;
        
    setuserSignUp({
        ...userSignUp,
        [name] : value
    })
  }
//   console.log(userSignUp)
  const handleSubmit = (e) => {

    e.prevenDefault();
     fetch(`http://localhost:3001/user`,{
         method:"POST",
         body:JSON.stringify(userSignUp),
         headers:{
             "content-type":"application/json"
         }
     })
  };

  const {name,email,password} = userSignUp;


  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h3>Sign up</h3>
        <input type="text" placeholder="Name" name="name" value={name} onChange={handleChange}/>
        <br />
        <input type="text" placeholder="Email" name="email" value={email} onChange={handleChange}/>
        <br />
        <input type="password" placeholder="Password" name="password" value={password} onChange={handleChange}/>
        <br />
        <input type="checkbox" /> <label htmlFor=""> Send me a weekly mix of handpicked projects, plus occasional Kickstarter news</label>
        <input type="checkbox" /> <label htmlFor="">Contact me about participating in Kickstarter research</label>
        <input type="text" />
        <p>
          By signing up, you agree to our
          <a href="#">Privacy Policy,Cookie Policy</a> and 
          <a href="#">Terms of Use.</a> and
          <a href="#">Read more</a>
        </p>
      </form>
      {/* <Link to="/login"><button>Redirect to Login</button></Link> */}
    </div>
  );
};

export default SignUp;
