import React,{useState} from "react";
import { Link } from "react-router-dom";

const SignUp = () => {
  const [userSignUp, setuserSignUp] = React.useState({
    name: "",
    email: "",
    password: "",
  });

  const handleChange = (e) =>{
    const {name, value} = e.target;
        
    setuserSignUp({
        ...userSignUp,
        [name] : value
    })
  }

  const handleSubmit = (e) => {
    e.prevenDefault();
  };

  const {name,email,password} = userSignUp;


  return (
    <>
      <form onSubmit={handleSubmit}>
        <h3>Sign up</h3>
        <input type="text" placeholder="Name" value={name} onChange={handleChange}/>
        <br />
        <input type="text" placeholder="Email"  value={userSignUp.email} onChange={handleChange} />
        <br />
        <input type="password" placeholder="Password" value={userSignUp.password} onChange={handleChange} />
        <br />
        <input type="checkbox" />
        <input type="checkbox" />
        <button>Create account</button>
      </form>
      {/* <Link to="/login"><button>Redirect to Login</button></Link> */}
    </>
  );
};

export default SignUp;
