import styled from 'styled-components'

export const Container = styled.div`
    width:28%;
    height: 600px;
    background-color: white;
    gap: 30px;
    border: 1px solid #f7e9e9;
    margin: auto;
    margin-top: 70px;

`
export const Button = styled.button`
    background-color:#028858;
    color: white;
    width: 78%;
    border-radius: 2px;
    padding: 7px;
    border: none;
`
export const Button1 = styled.button`
    background-color:black;
    color: white;
    width: 78%;
    border-radius: 7px;
    padding: 7px;
    border: none;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    margin-top: 10px;
`
export const Button2 = styled.button`
    background-color: #39579A;
    color: #FFFFFF;
    width: 78%;
    border-radius: 2px;
    padding: 7px;
    border: none;
    margin: 10px;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`
export const GetNotified = styled.p`
    color: #656969;
    font-size: small;
    
`
export const ReadMore = styled.p`
    color: #4C6CF8;
    margin-left: -60%;
    margin-top: -4%;
    cursor: pointer;
    font-weight:600;
    font-size:small;
`
export const LoginText = styled.h5`
    margin-left: -60%;
    font-size:x-large;
`

export const Checkbox = styled.p`
    margin-left: -50%;
`
export const EmailInput = styled.button`
    width: 78%;
    margin:10px;
`
export const PassInput = styled.button`
    width: 78%;
    margin:10px;
`
export const Forget_password = styled.p`
    margin-left: -47%;
    color: #152a88;
    cursor: pointer;
    font-weight: 500;
    font-size:small;
`
export const NewTo = styled.p`
    color: black;
    font-size: medium;
`
export const SignUpText = styled.p`
     color: #156288;
     cursor: pointer;
     display: inline-block
`

export const P = styled.p`
     color: #1a38e0;
     cursor: pointer;
     display: inline-block;
     font-weight: 500;
`
export const Checkbox1 = styled.p`
    margin-left: 1%;
    font-weight: 400;
`
export const Checkbox2 = styled.p`
    margin-left: 1%;
    font-weight: 400;

`
export const StyledInput = styled.input` 
    width: 78%;
    padding: 5px;
    margin: 5px;
    border: gray;
    & :input:focus{
        color: #028858;
    }
 `
