import React from 'react'
import { Container } from './Style.module'


 const Login = () => {
    const [formDetails, setFormDetails] = React.useState({
        email: "",
        password: ""
    })

    const handleChange = (e) =>{
        const {name, value} = e.target;
        
        setFormDetails({
            ...formDetails,
            [name] : value
        })

    }

    const handleSubmit = (e) =>{
        e.prevenDefault();
    }

    const {email,password} = formDetails;
  return (
    <div>
        <Container>
            <form onSubmit = {handleSubmit}>
                <h3>Log in</h3>
                <input type="text" placeholder='Email' value={email} onChange={handleChange}/>
                <br/>
                <input type="text" placeholder='password' value={password} onChange={handleChange}/>
                <br/>
                <button>Log in</button> <br/>
                <input type="checkbox" /><label htmlFor="">Remember me</label>
            </form>
        </Container>
    </div>
    
  )
}

export default Login;
